function maximum (a)
    local mi = 1             -- 最大值索引
    local m = a[mi]          -- 最大值
    for i,val in ipairs(a) do
        if val > m then
            mi = i
            m = val
        end
    end
    return m, mi
end

print(maximum({8,10,23,12,5}))

-- 可变参数
local function add(...) -- ... 三点表示可变参数列表
    local s = 0
    print(select("#",...)) -- select 获取输入个数
    local arg = {...} -- 需要使用 {} 作为构造式
    for i, v in ipairs(arg) do   --> {...} 表示一个由所有变长参数构成的数组
        s = s + v
    end
    return s
end

print(add(3,4,5,6,7))  --->25

-- 固定参数必须放在变长参数之前
function fwrite(fmt, ...)  ---> 固定的参数fmt
return io.write(string.format(fmt, ...))
end

fwrite("hello\n")       --->fmt = "runoob", 没有变长参数。
fwrite("%d%d\n", 1, 2)   --->fmt = "%d%d", 变长参数为 1 和 2

